import React, { useState, useRef, useEffect } from "react";
import { FaUserCircle } from "react-icons/fa";
import { useAuth } from "../context/AuthContext";
import ProfileModal from "./ProfileModal";
import SettingsModal from "./SettingsModal";
import ChangeNameModal from "./ChangeNameModal";
import LoginModal from "./LoginModal";
import "./Header.css";

export default function Header({ title }) {
  const { user, logout } = useAuth();

  const [menuOpen, setMenuOpen] = useState(false);
  const [openLogin, setOpenLogin] = useState(false);
  const [openProfile, setOpenProfile] = useState(false);
  const [openSettings, setOpenSettings] = useState(false);
  const [openChangeName, setOpenChangeName] = useState(false);

  const menuRef = useRef(null);

  // Fecha o menu se clicar fora
  useEffect(() => {
    function handleClickOutside(e) {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const username = user?.username || "Visitante";

  return (
    <>
      <header className="header">
        <h1 className="header-title">{title}</h1>

        <div className="header-right" ref={menuRef}>
          {user ? (
            <>
              <div
                className="user-section"
                onClick={() => setMenuOpen((prev) => !prev)}
              >
                <FaUserCircle className="user-icon" />
                <span className="username">{username}</span>
              </div>

              {menuOpen && (
                <div className="user-menu">
                  <p
                    className="menu-item"
                    onClick={() => {
                      setOpenProfile(true);
                      setMenuOpen(false);
                    }}
                  >
                    Perfil
                  </p>
                  <p
                    className="menu-item"
                    onClick={() => {
                      setOpenSettings(true);
                      setMenuOpen(false);
                    }}
                  >
                    Configurações
                  </p>
                  <p
                    className="menu-item"
                    onClick={() => {
                      setOpenChangeName(true);
                      setMenuOpen(false);
                    }}
                  >
                    Alterar nome
                  </p>
                  <p
                    className="menu-item logout"
                    onClick={() => {
                      logout();
                      setMenuOpen(false);
                    }}
                  >
                    Sair
                  </p>
                </div>
              )}
            </>
          ) : (
            <button className="login-btn" onClick={() => setOpenLogin(true)}>
              <FaUserCircle className="user-icon" />
              <span>Entrar</span>
            </button>
          )}
        </div>
      </header>

      {/* Modais */}
      {openLogin && <LoginModal onClose={() => setOpenLogin(false)} />}

      {openProfile && (
        <ProfileModal onClose={() => setOpenProfile(false)} />
      )}

      {openSettings && (
        <SettingsModal onClose={() => setOpenSettings(false)} />
      )}

      {openChangeName && (
        <ChangeNameModal onClose={() => setOpenChangeName(false)} />
      )}
    </>
  );
}
