import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { FaUserCircle } from "react-icons/fa";
import "./Header.css";

export default function Header() {
  const { user, logout } = useAuth();
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="topbar">
      <div className="topbar-left">
        <h1 className="topbar-title">Dashboard Geral</h1>

        <div className="btn-group">
          <button className="btn btn-gray">SIGA & SEJA SEGUIDO</button>
          <button className="btn btn-gray">CONECTAR CANAIS</button>
          <button className="btn btn-gray">VERIFICAR ATUALIZAÇÕES</button>
        </div>
      </div>

      <div className="topbar-right">
        <button className="btn tutorial">TUTORIAL</button>
        <button className="btn ajuda">AJUDA</button>

        <div className="user-box" onClick={() => setMenuOpen(!menuOpen)}>
          <FaUserCircle className="user-icon" />
          <span>{user?.username || "Usuário"}</span>
        </div>

        {menuOpen && (
          <div className="user-menu">
            <button className="menu-item">Minha Conta</button>
            <button className="menu-item">Minhas Streams</button>
            <button className="menu-item">Configurações</button>
            <button className="menu-item logout" onClick={logout}>
              Sair
            </button>
          </div>
        )}
      </div>
    </header>
  );
}
