import React, { createContext, useContext, useEffect, useState } from "react";

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [settings, setSettings] = useState({
    theme: "dark",   // "dark" ou "light"
    language: "pt",  // "pt" ou "en"
  });

  // Carregar do localStorage
  useEffect(() => {
    const savedUser = localStorage.getItem("streamcast_user");
    const savedSettings = localStorage.getItem("streamcast_settings");

    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    }
  }, []);

  // Salvar sempre que mudar
  useEffect(() => {
    if (user) {
      localStorage.setItem("streamcast_user", JSON.stringify(user));
    } else {
      localStorage.removeItem("streamcast_user");
    }
  }, [user]);

  useEffect(() => {
    localStorage.setItem("streamcast_settings", JSON.stringify(settings));
  }, [settings]);

  function login(username) {
    const newUser = { username };
    setUser(newUser);
  }

  function logout() {
    setUser(null);
  }

  function updateName(newName) {
    if (!user) return;
    const updated = { ...user, username: newName };
    setUser(updated);
  }

  function updateSettings(newSettings) {
    setSettings((prev) => ({ ...prev, ...newSettings }));
  }

  return (
    <AuthContext.Provider
      value={{ user, login, logout, updateName, settings, updateSettings }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
