import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { FaUserCircle } from "react-icons/fa";
import "./Header.css";

export default function Header() {
  const { user, logout } = useAuth();
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="header">
      <h1 className="header-title">Dashboard Geral</h1>

      <div className="header-user">
        <div className="user-info" onClick={() => setMenuOpen(!menuOpen)}>
          <FaUserCircle className="user-icon" />
          <span>{user?.username || "Usuário"}</span>
        </div>

        {menuOpen && (
          <div className="user-dropdown">
            <button className="dropdown-item">Minha Conta</button>
            <button className="dropdown-item">Minhas Streams</button>
            <button className="dropdown-item">Configurações</button>
            <button className="dropdown-item logout" onClick={logout}>
              Sair
            </button>
          </div>
        )}
      </div>
    </header>
  );
}
